using System.Collections;using System.Collections.Generic;using UnityEngine;public class LandmarkFinder : MonoBehaviour {    OVRCameraRig rig;    Transform camTransform;    Logger logger;    Timer timer;    HashSet<string> colliderTags;    HashSet<string> foundObjs;    float range = 20;	void Start () {        rig = FindObjectOfType<OVRCameraRig>();        camTransform = rig.centerEyeAnchor;        colliderTags = new HashSet<string>();        foundObjs = new HashSet<string>();        //add tags here        colliderTags.Add("Fountain");        colliderTags.Add("Skeleton");        colliderTags.Add("Lion");        colliderTags.Add("Gargoyle");        logger = this.transform.GetComponentInParent<Logger>();        timer = this.transform.GetComponentInParent<Timer>();        timer.startTimer();	}	    // Currently logs landmarks only once per game	void Update () {        RaycastHit hit;        camTransform = rig.centerEyeAnchor; //update cam direction        Vector3 rayDirection = camTransform.TransformDirection(Vector3.forward);        Vector3 rayStart = camTransform.position + rayDirection;             Debug.DrawRay(rayStart, rayDirection * range, Color.green);        if (Physics.Raycast(rayStart, rayDirection, out hit, range))        {            bool landmarkFound = colliderTags.Contains(hit.collider.tag);            bool alreadyFound = foundObjs.Contains(hit.collider.tag);            if(landmarkFound && !alreadyFound){   //found landmark                logger.LogTime("Landmark " + hit.collider.tag + " found",                                timer.getTime());                foundObjs.Add(hit.collider.tag);            }        }    }}