using System.Collections;using System.Collections.Generic;using UnityEngine;public class Logger : MonoBehaviour {    string path;    System.IO.StreamWriter writer;    public Timer GameTimer;    public bool LOGGING = false;    // Use this for initialization    void Start () {        GameTimer.startTimer();        if (LOGGING)        {            path = "Assets/Resources/log_" + System.DateTime.Now.ToString("dd-MM-yyyy_hh-mm-ss") + ".txt";            //path = "Assets/Resources/log_" + System.DateTime.Now + ".txt";            //path = "Assets/Resources/myLog.txt";            writer = new System.IO.StreamWriter(path, true);            writer.WriteLine(System.DateTime.Now + ":: Begin log file");            writer.WriteLine("-----------------------------------------");            writer.Close();        }    }		// Update is called once per frame	void Update () {        //Debug.Log(GameTimer.getTime());        //Debug.Log(System.DateTime.UtcNow + ":: cur time");	}    //Logs param time to log.txt file in Assets/Resources    public void Log(string message, float time)    {        if (LOGGING)        {            //Write to file            writer = new System.IO.StreamWriter(path, true);            writer.WriteLine(System.DateTime.Now + ":: " + message + " at time = " + GameTimer.getTime());            writer.Close();        }    }    public bool IsLogging()    {        return LOGGING;    }}